import React, { useState } from "react";

import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import uuid from "uuid/v4";

// Create a new project or label
const AddDialog = ({
  dispatchMenuItems,
  items,
  label,
  type,
  open,
  handleClose
}) => {
  const [inputValue, setInputValue] = useState("");
  const [alertDialog, setAlertDialog] = useState(false);

  const handleSubmit = event => {
    // Check if item already exists
    if (inputValue) {
      if (!items.some(item => item.name == inputValue)) {
        dispatchMenuItems({
          type: `ADD_${type}`,
          id: uuid(),
          name: inputValue,
          menu: type
        });
      } else {
        setAlertDialog(true);
      }
    }

    setInputValue("");
    event.preventDefault();
  };

  const handleChange = event => {
    setInputValue(event.target.value);
  };

  const handleAlertDialogClose = () => {
    setAlertDialog(false);
  };

  return (
    <div>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="form-dialog-title"
      >
        <DialogTitle id="form-dialog-title">{`${label} name`}</DialogTitle>
        <DialogContent>
          <DialogContentText>{`Please enter the ${label.toLowerCase()} name`}</DialogContentText>
          <form id="projectName" onSubmit={handleSubmit}>
            <TextField
              margin="dense"
              id="name"
              label={`${label} Name`}
              type="text"
              fullWidth
              value={inputValue}
              onChange={handleChange}
            />
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button
            onClick={handleClose}
            color="primary"
            type="submit"
            form="projectName"
          >
            Confirm
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog
        open={alertDialog}
        onClose={handleAlertDialogClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {`This ${label.toLowerCase()} already exists.`}
        </DialogTitle>
        <DialogActions>
          <Button onClick={handleAlertDialogClose} color="primary">
            Ok
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default AddDialog;
