import React ,{useState ,useEffect} from "react";
import { Task } from "./Task";

export const AddProject = () => {
    const [prolist, setproList] = React.useState([]);
    const [project, setProject] = React.useState("");
    const [projectEditing, setProjectEditing] = useState(null);
    const [editingText, setEditingText] = useState("");




    function handleSubmit1(e) {
      e.preventDefault();

      const newproject = {
        id1: new Date().getTime(),
        text: project,
        completed: false,
      };
      setproList([...prolist].concat(newproject));
      setProject("");
    }

    function deleteproject1(id) {
      let updatedprolist = [...prolist].filter((project) => project.id !== id);
      setproList(updatedprolist);
    }

    function toggleComplete1(id) {
      let updatedprolist = [...prolist].map((project) => {
        if (project.id === id) {
          project.completed = !project.completed;
        }
        return project;
      });
      setproList(updatedprolist);
    }

    function submitEdits1(id) {
      const updatedprolist = [...prolist].map((project) => {
        if (project.id === id) {
          project.text = editingText;
        }
        return project;
      });
      setproList(updatedprolist);
      setProjectEditing(null);
    }

    return (
      <div id="project-prolist">
        <h1>project list</h1>
        <form onSubmit={handleSubmit1}>
          <input
            type="text1"
            onChange={(e) => setProject(e.target.value)}
            value={project}
          />
          <button type="submit1">Add Project</button>
          <input onClick={(e) => Task}></input>

        </form>

        {prolist.map((project) => (
          <div key={project.id} className="project">
            <div className="project-text1"
                type="checkbox1"
                id="complete"
                checked={project.completed}
                onChange={() => toggleComplete1(project.id)}
                //style={{textDecoration : project.id ? "line-through": ""}}
              >
              {project.id === projectEditing ? (
                <input
                  type="text1"
                  onChange={(e) => setEditingText(e.target.value)}
                />
              ) : (
                <div>{project.text}</div>
              )}
            </div>
            <div className="project-actions">
              {project.id === projectEditing ? (
                <button onClick={() => submitEdits1(project.id)}>Submit Edits</button>
              ) : (
                <button onClick={() => setProjectEditing(project.id)}>Edit</button>
              )}

              <button onClick={() => deleteproject1(project.id)}>Delete</button>

            </div>

          </div>
          ))}
      </div>
    );
  };
