import React, { Fragment, useState, useEffect ,useRef} from "react";

import { makeStyles } from "@material-ui/core/styles";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import Checkbox from "@material-ui/core/Checkbox";
import DeleteForeverIcon from "@material-ui/icons/DeleteForever";
import IconButton from "@material-ui/core/IconButton";
import Divider from "@material-ui/core/Divider";
import MoreIcon from "@material-ui/icons/MoreVert";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import Grid from "@material-ui/core/Grid";
import EditIcon from '@material-ui/icons/Edit';
import uuid from "uuid/v4";

import SelectedDialog from "../../SelectDialog";



const useStyles = makeStyles(() => ({
  listItem: {
    paddingLeft: 0
  }
}));

const menuOptions = ["Labels"];

const TaskItem = ({ dispatch, task, labels, setTask}) => {
  const classes = useStyles();

  const [anchorEl, setAnchorEl] = useState(null);
  const [openDialog, setOpenDialog] = React.useState(false);

  const [selectedIndex, setSelectedIndex] = useState(0);

  const [disabled, setDisabled] = useState(false);
  const [todoEditing, setTodoEditing] = React.useState(null);
  const [editingText, setEditingText] = React.useState("");
  const [drawerMobileOpen, setDrawerMobileOpen] = useState(false);


  React.useEffect(() => {
    const json = localStorage.getItem("task");
    const loadedTodos = JSON.parse(json);
    if (loadedTodos) {
      setTask(loadedTodos);
    }
  }, []);

  React.useEffect(() => {
    const json = JSON.stringify(task);
    localStorage.setItem("todos", json);
  }, [task]);


  useEffect(() => {
    const disable = task.completed || task.deleted;
    setDisabled(disable);
  }, [task.completed, task.deleted]);

  const handleClickCheckBox = () => {
    dispatch({ type: task.completed ? "UNDO_TODO" : "DO_TODO", id: task.id });
  };

  const handleClickDelete = () => {
    dispatch({ type: "DELETE_TODO", id: task.id });
  };

  const handleClickMore = event => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuMoreClick = (event, index) => {
    setSelectedIndex(index);
    setAnchorEl(null);
    setOpenDialog(true);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

 const handleSubmitEdits = event => {
    const updatedTodos = [task].map((task) => {
      if (task.name === event) {
        task.text = editingText;
      }
      return task;
    });
    dispatch(updatedTodos);
    setTodoEditing(null);
    console.log(task);
  }
  const handleDrawerToggle = (task) => {
    setDrawerMobileOpen(!drawerMobileOpen);
    setTodoEditing(task.id);
  };


  return (
    <Fragment>
      <ListItem dense className={classes.listItem} disabled={disabled}>
        <ListItemIcon>
          <Checkbox
            edge="start"
            tabIndex={-1}
            onClick={handleClickCheckBox}
            checked={disabled}
          />
        </ListItemIcon>
        <ListItemText primary={task.name} />
        <IconButton
          aria-label="display more actions"
          edge="end"
          color="inherit"
          onClick={handleClickMore}
        >
          <MoreIcon />
        </IconButton>
        <Menu
          id="lock-menu"
          anchorEl={anchorEl}
          keepMounted
          open={Boolean(anchorEl)}
          onClose={handleClose}
        >
          {menuOptions.map((option, index) => (
            <div key={uuid()}>
              <MenuItem onClick={() => handleMenuMoreClick( index)}>
                {option}
              </MenuItem>
            </div>
          ))}
        </Menu>
        <SelectedDialog
          openDialog={openDialog}
          handleClose={handleCloseDialog}
          title={menuOptions[selectedIndex]}
          dispatch={dispatch}
          taskId={task.id}
          labels={labels}
        />
        <IconButton>
          <EditIcon onClick ={() => handleDrawerToggle(task )}></EditIcon>
           {task.id === todoEditing &&  drawerMobileOpen ? (

              <input
                type="text"
                onChange={(e) => setEditingText(e.target.value)}
              />
            ) : (
              <div>{task.text}</div>
            )}

            {task.id === todoEditing ? (
              <button onClick={() => handleSubmitEdits(task.id)}>Submit Edits</button>
            ) : (
              <button onClick={() => setTodoEditing(task.id)}>Edit</button>
            )}
        </IconButton>



        <ListItemSecondaryAction onClick={handleClickDelete}>
          <IconButton edge="end" aria-label="delete">
            <DeleteForeverIcon />
          </IconButton>
        </ListItemSecondaryAction>

      </ListItem>
      <Divider />
    </Fragment>
  );
};

export default TaskItem;
